(function () {
'use strict';

window.addEventListener('load', function () {
  var checkbox = document.querySelector('.terms_checkbox');
  var button = document.querySelector('.terms_button_anchor');
  var flag = false;
  checkbox.addEventListener('click', function () {
    if (!flag) {
      this.classList.add('o-checked');
      button.classList.add('o-clickable');
      flag = true;
    } else {
      this.classList.remove('o-checked');
      button.classList.remove('o-clickable');
      flag = false;
    }
  });

  button.addEventListener('click', function () {
    if (this.classList.contains('o-clickable')) {
      location.href = '/mama/archive/list.html';
    }
  });
});

}());

